<!DOCTYPE html><html>    <head>	<meta charset="UTF-8">        <meta name="viewport" content="width=device-width, initial-scale=1.0">        <meta name="author" content="Quentin SCHAU"/>        <link rel="stylesheet" href="./CSS/styles.css">    </head>	<body class='inscription'>            <?php                $type_libelle = $_POST['type_libelle'];                try{                    //On se connecte à la BDD                    $dbco = new PDO("mysql:host=localhost;dbname=mesappartements","root","");                    $dbco->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);                    //On verifie que le type n'existe pas                     $reponse = $dbco->prepare("SELECT COUNT(*) FROM typeappartement WHERE libelle = ?");                    $reponse->execute([$type_libelle]);                    $compte = $reponse->fetch(PDO::FETCH_ASSOC)["COUNT(*)"];                    if ($compte>0){                        echo "<p>type d'appartement deja existant</br>";                        echo "<a href = \"Ajout_type_appart.html\"><input type=\"button\" value=\"retour\"></a></p>";                    }else{                    //Sinon on insère les données reçues                    $sth = $dbco->prepare("                            INSERT INTO typeappartement( Libelle)                            VALUES( ? )");                    $sth->execute([$type_libelle]);                    $sth->CloseCursor();                    $reponse->CloseCursor();                    header("Location:GestionMaison.php");                    }                }                catch(PDOException $e){                        echo 'Impossible de traiter les données. Erreur : '.$e->getMessage();                }            ?>	</body></html>